import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";

import { get_all_countries } from "../redux/action";

import CardCountry from './CardCountry';

import "../css/Countries.css";

function Countries() {

  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);

  const [back, setBack] = useState(0);
  const [next, setNext] = useState(10);
  const [id, setId] = useState(1);

  // const [activo, setActivo] = useState(false);

  let contadorCountries = countries.length;
  let contadorPage = countries.length / 10;
  let items = 10;

  const onclickSelectPage = (id) => {
    setNext(items * id);
    setBack((items * id) - items);
    setId(id);
  }
  const onclickBackPage = () => {
    if (id <= 1) return
    setId(id - 1);
    setNext(next - items);
    setBack(back - items);
  }
  // const onclickNextPage = () => {
    
  //   if (id < contadorPage) {
  //     setId(id + 1);
  //     setNext(next + items);
  //     setBack(((back-1) + items));
  //   }
  // }

  useEffect(() => {
    !countries.length && dispatch(get_all_countries());
  }, [countries, dispatch, back]);

  let onclickNextPage;
  useEffect(() => {
    const eventoclick = document.addEventListener('click', onclickNextPage = () => {
      console.log("estoy en el enevto click")
    })
  }, [back]);

  return (
    <React.Fragment>
      <div className="paginado">
        <button className='change_page' name='backnumber'
          onClick={onclickBackPage}
        >{'<<'}</button>
        <div className='content_btn_page'>
          {
            countries.length && countries.map((_, i) => {
              if (i < contadorCountries - (contadorCountries - 5)) {
                return (
                  <button
                    name='pagenumber' key={i} id={i + 1}
                    onClick={() => onclickSelectPage(i + 1)}
                    disabled={id === (i + 1) ? true : false}
                    className={id === (i + 1) ? 'btn_page activo' : 'btn_page'}
                  >
                    {i + 1}
                  </button>)
              }
              return null
            })
          }
        </div>
        <button className='change_page' name='nextnumber'
          onClick={onclickNextPage}
        >{'>>'}</button>

      </div>

      <div className='content_countries'>
        {
          countries.length && countries.map((c) => {
            return (
              <CardCountry key={c.id} id={c.id} imagen={c.imagen} nombre={c.name} continente={c.continente} poblacion={c.poblacion} />
            )
          }).slice(back, (next - 1))
        }
      </div>
    </React.Fragment>
  )
}

export default Countries;
