import React, { useState } from 'react';

import "../css/FormActivity.css";

import { FaSearch } from "react-icons/fa";

function CardFormActivity({ paises }) {

  const [pais, setPais] = useState([]);
  const [input, setInput] = useState("");
  const [listaPaises, setListaPaises] = useState([]);

  const onChangeInput = (e) => {
    if(!e.target.value.length) return setPais([])
    const result = paises.map((p) => {
      if(p.name.toLowerCase().includes(e.target.value)){
        return {
          id: p.id,
          name: p.name
        }
      }
    });
    const filtrar = result.filter((f) => f !== undefined)
    setPais(filtrar)
    console.log("lista: ", pais)
  };

  const onClickListaPaises = (e) => {
    setInputs((prev) => {
      return {
        ...prev,
        tipos: prev.tipos.filter((f) => f !== Number(e.target.id))
      }
    });
  };

  const onclik_new_activity = (ev) => {
    ev.preventDefault();
    console.log("algo que enviar")
  }
  return (
    <div className='content_form'>
      <h2>Formulario de creaci√≥n de Actividades</h2>
      <div className='formulario'>
        <div className='descripcion'>
          <form className='form_description' method='post' onSubmit={onclik_new_activity}>
            <label>Nombre: </label>
            <input type='text' />
            <label>Dificultad: </label>
            <input type='range' min={1} max={5} step={1} />
            <label>Duracion: </label>
            <input type='number' className='numero' />
            <label>Temporada: </label>
            <input type='text' />
            <div className='content_btn_form'>
              <button type='button'>Regresar</button>
              <button type='submit'>Grabar</button>
              <button type='button'>Limpiar</button>
            </div>
          </form>

        </div>
        <div className='content_lista_paises'>
          <div className='content_buscar'>
            <input type='text' onChange={onChangeInput}/>
            <button ><FaSearch /></button>
          </div>
          <div className='list_pais'>

            <select className='select_pais' onClick={onClickListaPaises}>

              {
                pais.length && pais.map((p) => {
                  return (
                    <option key={p.id}>{p.name}</option>
                  )
                })
              }
            </select>

          </div>
          <div className='list_pais'>
              lista de paises
          </div>
        </div>
      </div>

    </div>
  )
}

export default CardFormActivity;
