import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";

import { get_all_countries } from "../redux/action";

import CardCountry from './CardCountry';

import "../css/Countries.css";

function Countries() {

  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);

  const [next, setNext] = useState(10);
  const [back, setBack] = useState(0);
  const [nextNumber, setNextNumber] = useState(5);
  const [backNumber, setBackNumber] = useState(0);

  const [activo, setActivo] = useState('btn_page activo');

  let contadorPage = countries.length / 10;

  const onClickBackPage = (e) => {

    if (backNumber === 0) {
      setBack(0);
      setNext(10);
      return
    }
    if (backNumber === contadorPage) {
      setBackNumber(backNumber - 5);
      setNextNumber(nextNumber - 1);
      return
    }
    setBackNumber(backNumber - 5);
    setNextNumber(nextNumber - 5);
    setBack((backNumber * 10) - 1);
    setNext((backNumber * 10) + 10);

  }

  const onClickNextPage = (e) => {

    if (nextNumber === contadorPage) {
      setBackNumber(nextNumber);
      setNextNumber(nextNumber + 1);
      setBack((nextNumber * 10) - 1);
      setNext((nextNumber * 10) + 1);
      return
    }

    if (nextNumber > contadorPage) return

    setBackNumber(backNumber + 5);
    setNextNumber(nextNumber + 5);
    setBack((nextNumber * 10) - 1);
    setNext((nextNumber * 10) + 10);

  }

  const onClickChangePage = (e) => {
    
    if(e.target.id === '1'){
      setBack(0);
      setNext(10);
      e.target.className = 'btn_page activo'  
      return
    }

    setBack((Number(e.target.id) * 10) - 11);
    setNext((Number(e.target.id) * 10));
  };

  useEffect(() => {
    !countries.length && dispatch(get_all_countries());
  }, [countries, dispatch, back]);

  return (
    <React.Fragment>
      <div className="paginado">
        <button className='change_page' name='backnumber'
          onClick={onClickBackPage}
        >{'<<'}</button>
        <div className='content_btn_page'>
          {
            countries.length && countries.map((_, i) => {
              if (i >= backNumber && i < nextNumber) {
                return (<button className='' name='pagenumber' key={i} id={i + 1} onClick={onClickChangePage}>{i + 1}</button>)
              }
              return null
            })
          }
        </div>
        <button className='change_page' name='nextnumber'
          onClick={onClickNextPage}
        >{'>>'}</button>

      </div>

      <div className='content_countries'>
        {
          countries.length && countries.map((c) => {
            return (
              <CardCountry key={c.id} id={c.id} imagen={c.imagen} nombre={c.name} continente={c.continente} poblacion={c.poblacion} />
            )
          }).slice(back, (next - 1))
        }
      </div>
    </React.Fragment>
  )
}

export default Countries;
