import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";

import { get_all_countries } from "../redux/action";

import CardCountry from './CardCountry';

import "../css/Countries.css";
import Pagination from './Pagination';

function Countries() {

  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);

  const [backcountries, setBackCountries] = useState(0);
  const [nextcountries, setNextCountries] = useState(10);
  const [backpage, setBackPage] = useState(0);
  const [nextpage, setNextPage] = useState(5);

  const [id, setId] = useState(1);

  // const [activo, setActivo] = useState(false);

  let contadorCountries = countries.length;
  let contadorPage = countries.length / 10;
  let items = 10;

  const elementos = Array.from({ length: contadorPage }, (_, index) => index + 1);
  console.log(elementos)

  const onclickSelectPage = (id) => {
    setNextCountries(items * id);
    setBackCountries((items * id) - items);
    setId(id);
  }
  const onclickBackPage = () => {
    if (id <= 1) return
    setId(id - 1);
    setNextCountries(nextcountries - items);
    setBackCountries(backcountries - items);
  }
  const onclickNextPage = () => {
    
    if (id < contadorPage) {
      setId(id + 1);
      setNextCountries(nextcountries + items);
      setBackCountries(((backcountries-1) + items));
    }
  }

  useEffect(() => {
    !countries.length && dispatch(get_all_countries());
  }, [countries, dispatch]);


  return (
    <React.Fragment>
      <Pagination elementos={elementos} inicio={backpage} final={nextpage} id={id} onclickSelectPage={()=>{onclickSelectPage(id)}}/>
      <div className='content_countries'>
        {
          countries.length && countries.map((c) => {
            return (
              <CardCountry key={c.id} id={c.id} imagen={c.imagen} nombre={c.name} continente={c.continente} poblacion={c.poblacion} />
            )
          }).slice(backcountries, (nextcountries - 1))
        }
      </div>

    </React.Fragment>
  )
}

export default Countries;
