import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from "react-redux";

import { get_all_countries } from "../redux/action";

import CardCountry from './CardCountry';

import "../css/Countries.css";

function Countries() {

  const dispatch = useDispatch();
  const countries = useSelector((state) => state.countries);

  const [next, setNext] = useState(10);
  const [back, setBack] = useState(0);
  const [nextNumber, setNextNumber] = useState(5);
  const [backNumber, setBackNumber] = useState(0);
  const [index, setIndex] = useState(1);

  let contadorPage = countries.length / 10;
  // let contadorBtn = contadorPage / 5;

  const onClickChangePage = (e) => {

    if (e.target.name === 'nextnumber') {
      if (nextNumber === contadorPage) {
        setBackNumber(nextNumber);
        setNextNumber(nextNumber + 1);
        return
      }
      if (nextNumber > contadorPage) {
        return
      }
      setBackNumber(backNumber + 5);
      setNextNumber(nextNumber + 5);
      setIndex(index + 5);
    }

    if (e.target.name === 'backnumber') {
      console.log("entre al back 1: ", backNumber)
      if (backNumber === 0) {
        return
      }
      if (backNumber === contadorPage) {
        console.log("entre al back 2: ", backNumber)
        setBackNumber(backNumber - 5);
        setNextNumber(nextNumber - 1);
        return
      }
      console.log("entre al back 3: ", backNumber)
      setBackNumber(backNumber - 5);
      setNextNumber(nextNumber - 5);
    }

    if (index === 1) {
      setBack(0);
      setNext(10);
    }
    if (e.target.name === 'pagenumber') {
      setBack((index * 10) - 11);
      setNext((index * 10));
    }

  }

  useEffect(() => {
    !countries.length && dispatch(get_all_countries());
  }, [countries, dispatch, back]);

  return (
    <React.Fragment>
      <div className="paginado">
        <button name='backpage' onClick={onClickChangePage}>{'<<'}</button>
        <div className='content_btn_page'>
          <button name='backnumber' onClick={onClickChangePage}>{'<'}</button>
          {
            countries.length && countries.map((c, i) => {
              if (i >= backNumber && i < nextNumber) {
                return (<button name='pagenumber' key={i} id={i + 1} onClick={onClickChangePage} onChange={() => {setIndex(Number(e.target.id))}}>{i + 1}</button>)
              }
              return null
            })
          }
          <button name='nextnumber' onClick={onClickChangePage}>{'>'}</button>
        </div>
        <button name='nextpage' onClick={onClickChangePage}>{'>>'}</button>
      </div>

      <div className='content_countries'>
        {
          countries.length && countries.map((c, i) => {
            if (i >= back && i < next - 1) {
              return (
                <CardCountry key={c.id} id={c.id} imagen={c.imagen} nombre={c.name} continente={c.continente} poblacion={c.poblacion} />
              );
            }
            return null
          })
        }
      </div>
    </React.Fragment>
  )
}

export default Countries;
